import sampleData from './initialState';

var store = undefined;

export default {
    init(configureStore){
        store = configureStore(sampleData);
    },
    getStore(){
        return store;
    },
    getCurrentState(){
        return store.getState();
    },
    getAppConfig(){
        var currentState = this.getCurrentState();
        return currentState.config;
    }
};
